import './cardInfo.css';

function CardInfo(props) {
    const description = props.singlePodcast[props.singlePodcast.length - 1]?.shortDescription;
    const episodeDescription = props.singleEpisode[props.singleEpisode.length - 1]?.shortDescription;
    const descriptionText = (description || episodeDescription) ? (description || episodeDescription) : 'The podcast author has not provided a description to show';
    console.log(props)

    return (
        <article className="info">
            <img src={props.singlePodcast[props.singlePodcast.length - 1].artworkUrl600 } alt="" className="podcast-img" />
            <div className='info-podcast'>
                <h4 className="podcast-title">{props.singlePodcast[props.singlePodcast.length - 1].collectionName}</h4>
                <p className='podcast-artist'>By {props.singlePodcast[0].artistName || props.singleEpisode[0].artistName} </p>
            </div>
            <div className="podcast-description">
                <h4>Description:</h4>
                <p className="description-info">{descriptionText}</p>
            </div>
        </article>
    );
}

export default CardInfo;
