import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getSinglePodcast } from "../../services/podcast";
import './podcastDetail.css';

import HeaderEpisodes from "../../components/header-episodes/Header-Episodes";
import CardInfo from "../../components/card-info/CardInfo";
import PodcastData from "../../components/podcastData/PodcastData";

function PodcastDetail (props) {

    const params = useParams();
    const [singlePodcast, setSinglePodcast] = useState([]);

    const millisToTime = function (mis) {

        function addZ(n) {
          return (n<10? '0':'') + n;
        }

        const ms = mis % 1000;
        mis = (mis - ms) / 1000;
        const secs = mis % 60;
        mis = (mis - secs) / 60;
        const mins = mis % 60;
        const hors = (mis - mins) / 60;
        return addZ(hors) + ':' + addZ(mins) + ':' + addZ(secs);
      }
    const date = function (d) {
        const dateOnly = d.split('T')[0];
        const dateSort = dateOnly.split('-');
        const reversedDate = dateSort[2] + "-" + dateSort[1] + "-" + dateSort[0];
        return reversedDate;
    }

    useEffect(() => {
        getSinglePodcast(params.podcastId).then((response) => setSinglePodcast(response));
    }, [params.podcastId]);

    return (singlePodcast.length ? 
        <>
        <Header />
        <main className="main">
            <section className="main-section">
                <CardInfo singlePodcast={singlePodcast}/>
                <article className="episodes">
                <HeaderEpisodes singlePodcast={singlePodcast} />
                    {singlePodcast.map((podCast) => {
        return (    <PodcastData podCast={podCast} />  
    )
    })}         </article>
            </section>
        </main> 
        </>: <Error/>)
}

export default PodcastDetail;
