import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getSinglePodcast } from "../../services/podcast";
import Header from "../../components/header/Header";
import './episodes.css';
import CardInfo from "../../components/cardInfo/CardInfo";


function Episodes(props) {

    const params = useParams();
    const navigate = useNavigate();
    const [episode, setEpisode] = useState([]);
    const description = episode[episode.length - 1]?.shortDescription;
    const descriptionText = description ? description : 'The podcast author has not provided a description to show';

    useEffect(() => {
        getSinglePodcast(params.podcastId).then((response) => setEpisode(response));
    }, [params.podcastId]);

    const episodeIdParam = parseInt(params.episodeId);
    const singleEpisode = episode.find(ep => ep.trackId === episodeIdParam);
    console.log(singleEpisode)

    return (singleEpisode ? <> <Header />
        <main className="main">
            <section className="main-section">
                <article className="info" onClick={() => navigate(`/podcastDetail/${singleEpisode.collectionId}`)}>
                    <h4>{singleEpisode.collectionName}</h4>
                    <h4>{episode[0].artistName}</h4>
                    <img src={singleEpisode.artworkUrl600} alt="" className="podcast-img" />
                    <p>{descriptionText}</p>
                </article>
                <article className="episode-description">
                    <h4>{singleEpisode.trackName}</h4>
                    <p>{singleEpisode.description}</p>
                    <audio controls>
                        <source src={singleEpisode.previewUrl} type="audio/mpeg" />
                        Your browser does not support the audio element.
                    </audio>
                </article>
            </section>
        </main> </> : <div>nada que ver</div>)
}


export default Episodes;
